import { useState, useEffect } from "react";
import Axios from "axios";

export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/restaurants").then((res) => {
      setRestaurants(res.data);
    });
  }, []);

  const handleCitySelect = (event) => {
    setSelectedCity(event.target.value);
  };

  const handleRestaurantSelect = (event) => {
    const restaurantId = event.target.value;
    const selectedRestaurant = restaurants.find(
      (restaurant) => restaurant.id === restaurantId
    );
    setSelectedRestaurantCoords(selectedRestaurant.location);
    setSelectedRestaurant(restaurantId);
  };


  const [selectedRestaurantCoords, setSelectedRestaurantCoords] = useState({});

  useEffect(() => {
    if (selectedCity) {
      Axios.get(`http://localhost:3001/restaurants/${selectedCity}`).then(
        (res) => {
          setRestaurants(res.data);
        }
      );
    }
  }, [selectedCity]);

  return (
    <>
      <select value={selectedCity} onChange={handleCitySelect}>
        <option value="">Select a city</option>
        <option value="Casablanca">Casablanca</option>
        <option value="Tanger">Tanger</option>
        <option value="El Jadida">El jadida</option>
        {/* add more cities as needed */}
      </select>

      <select value={selectedRestaurant} onChange={handleRestaurantSelect}>
        <option value="">Select a restaurant</option>
        {restaurants.map((restaurant) => (
          <option value={restaurant.id} key={restaurant.id}>
            {restaurant.title}
          </option>
        ))}
      </select>
      <MapContainer
  center={[selectedRestaurantCoords.lat, selectedRestaurantCoords.lng]}
  zoom={13}
  scrollWheelZoom={false}
  style={{ height: "400px", width: "100%" }}
>
  <TileLayer
    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
    attribution='Map data Â© <a href="https://openstreetmap.org">OpenStreetMap</a> contributors'
  />
  <Marker position={[selectedRestaurantCoords.lat, selectedRestaurantCoords.lng]} />
</MapContainer>

    </>
  );
}
