import { useState, useEffect } from "react";
import Axios from "axios";

export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");

  useEffect(() => {
    if (selectedCity) {
      Axios.get(`http://localhost:3001/restaurants/${selectedCity}`).then((res) => {
        setRestaurants(res.data);
      });
    } else {
      setRestaurants([]);
    }
  }, [selectedCity]);

  const handleCitySelect = (event) => {
    setSelectedCity(event.target.value);
    setSelectedRestaurant("");
  };

  const handleRestaurantSelect = (event) => {
    setSelectedRestaurant(event.target.value);
  };

  return (
    <>
      <label htmlFor="cities">Select a city:</label>
      <select id="cities" value={selectedCity} onChange={handleCitySelect}>
        <option value="">Select a city</option>
        <option value="Casablanca">Casablanca</option>
        <option value="Tanger">Tanger</option>
        <option value="El Jadida">El Jadida</option>
      </select>
      {restaurants.length > 0 && (
        <>
          <label htmlFor="restaurants">Select a restaurant:</label>
          <select id="restaurants" value={selectedRestaurant} onChange={handleRestaurantSelect}>
            <option value="">Select a restaurant</option>
            {restaurants.map((restaurant) => (
              <option value={restaurant.title} key={restaurant._id}>
                {restaurant.title}
              </option>
            ))}
          </select>
        </>
      )}
    </>
  );
}
