import { useState, useEffect } from "react";
import Axios from "axios";
import Header from "./Header";
import Footer from "./Footer";

export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");

  const [selectedZone, setSelectedZone] = useState("");

const handleZoneSelect = (event) => {
  setSelectedZone(event.target.value);
};

useEffect(() => {
  if (selectedCity) {
    Axios.get(`http://localhost:3001/restaurants/${selectedCity}`).then(
      (res) => {
        let filteredRestaurants = res.data;
        if (selectedZone) {
          // filter restaurants based on selected zone
          const totalRestaurants = filteredRestaurants.length;
          const numToShow = Math.ceil(totalRestaurants / 5);
          filteredRestaurants = filteredRestaurants.filter(
            (_, index) =>
              Math.floor(index / numToShow) ===
              Math.floor(Math.random() * numToShow)
          );
        }
        setRestaurants(filteredRestaurants);
      }
    );
  }
}, [selectedCity, selectedZone]);

return (
  <>
    <Header />
    <select value={selectedCity} onChange={handleCitySelect}>
      <option value="">Select a city</option>
      <option value="Casablanca">Casablanca</option>
      <option value="Tanger">Tanger</option>
      <option value="El Jadida">El jadida</option>
      {/* add more cities as needed */}
    </select>
    <select value={selectedZone} onChange={handleZoneSelect}>
      <option value="">Select a zone</option>
      <option value="1">Zone 1</option>
      <option value="2">Zone 2</option>
      <option value="3">Zone 3</option>
      <option value="4">Zone 4</option>
    </select>
    <select value={selectedRestaurant} onChange={handleRestaurantSelect}>
      <option value="">Select a restaurant</option>
      {restaurants.map((restaurant) => (
        <option value={restaurant.id} key={restaurant.id}>
          {restaurant.title}
        </option>
      ))}
    </select>
    <Footer />
  </>
);
      }
