import { useState, useEffect } from "react";
import Axios from "axios";
import Header from "./Header";
import Footer from "./Footer";
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';


export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedZone, setSelectedZone] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");

  useEffect(() => {
    Axios.get("http://localhost:3001/restaurants").then((res) => {
      setRestaurants(res.data);
    });
  }, []);

  const handleCitySelect = (event) => {
    setSelectedCity(event.target.value);
    setSelectedZone("");
    setSelectedRestaurant("");
  };

  const handleZoneSelect = (event) => {
    setSelectedZone(event.target.value);
    setSelectedRestaurant("");
  };

  const handleRestaurantSelect = (event) => {
    setSelectedRestaurant(event.target.value);
  };

  const [map, setMap] = useState(null);

  useEffect(() => {
    if (map === null) {
      const newMap = L.map('map').setView([31.633979, -8.009794], 13);
      L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: 'Map data &copy; <a href="https://www.openstreetmap.org/">OpenStreetMap</a> contributors',
        maxZoom: 18,
      }).addTo(newMap);
      setMap(newMap);
    }
  }, [map]);

  useEffect(() => {
    if (selectedRestaurant && map !== null) {
      const restaurant = restaurants.find((r) => r.id === selectedRestaurant);
      const marker = L.marker([restaurant.latitude, restaurant.longitude]).addTo(map);
      map.setView([restaurant.latitude, restaurant.longitude], 15);
      return () => {
        map.removeLayer(marker);
      };
    }
  }, [selectedRestaurant, map]);


  return (
    <>
      <Header />
      <div id="map" style={{ height: '300px' }}></div>
      <div>
      <select value={selectedCity} onChange={handleCitySelect}>
        <option value="">Select a city</option>
        <option value="Casablanca">Casablanca</option>
        <option value="Tanger">Tanger</option>
        <option value="El Jadida">El jadida</option>
        {/* add more cities as needed */}
      </select>
      <select value={selectedZone} onChange={handleZoneSelect}>
        <option value="">Select a zone</option>
        <option value="1">Zone 1</option>
        <option value="2">Zone 2</option>
        <option value="3">Zone 3</option>
        <option value="4">Zone 4</option>
      </select>
      <select value={selectedRestaurant} onChange={handleRestaurantSelect}>
        <option value="">Select a restaurant</option>
        {restaurants.map((restaurant) => (
          <option value={restaurant.id} key={restaurant.id}>
            {restaurant.title}
          </option>
        ))}
      </select>
      </div>
      <Footer />
    </>
  );
}
