import { useState, useEffect } from "react";
import Axios from "axios";
import GoogleMapReact from "google-map-react";
import { Container, Navbar, Nav } from "react-bootstrap";

const MapMarker = () => (
  <div style={{ fontSize: "30px" }}>
    <i className="fa fa-map-marker" aria-hidden="true"></i>
  </div>
);

export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");
  const [center, setCenter] = useState({
    lat: 35.7463373,
    lng: -5.8506911,
  });

  useEffect(() => {
    Axios.get("http://localhost:3001/restaurants").then((res) => {
      setRestaurants(res.data);
    });
  }, []);

  const handleCitySelect = (event) => {
    setSelectedCity(event.target.value);
  };

  const handleRestaurantSelect = (event) => {
    const selectedId = event.target.value;
    const selectedRestaurant = restaurants.find(
      (restaurant) => restaurant.id === selectedId
    );
    setCenter({
      lat: selectedRestaurant.location.lat,
      lng: selectedRestaurant.location.lng,
    });
    setSelectedRestaurant(selectedId);
  };

  useEffect(() => {
    if (selectedCity) {
      Axios.get(`http://localhost:3001/restaurants/${selectedCity}`).then(
        (res) => {
          setRestaurants(res.data);
        }
      );
    }
  }, [selectedCity]);

  return (
    <>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand href="#">React Restaurant Finder</Navbar.Brand>
          <Nav className="me-auto">
            <Nav.Link href="#">Home</Nav.Link>
            <Nav.Link href="#">About</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Container>
        <div style={{ height: "400px", width: "100%" }}>
          <GoogleMapReact
            bootstrapURLKeys={{
              key: "AIzaSyDnH1CSsDOQz5BKD2forEe9oFpNQKgTFwA",
            }}
            defaultCenter={center}
            defaultZoom={12}
          >
            {restaurants.map((restaurant) => (
              <MapMarker
                key={restaurant.id}
                lat={restaurant.location.lat}
                lng={restaurant.location.lng}
              />
            ))}
          </GoogleMapReact>
        </div>

        <select value={selectedCity} onChange={handleCitySelect}>
          <option value="">Select a city</option>
          <option value="Casablanca">Casablanca</option>
          <option value="Tanger">Tanger</option>
          <option value="El Jadida">El jadida</option>
          {/* add more cities as needed */}
        </select>

        <select value={selectedRestaurant} onChange={handleRestaurantSelect}>
          <option value="">Select a restaurant</option>
          {restaurants.map((restaurant) => (
            <option value={restaurant.id} key={restaurant.id}>
              {restaurant.title}
            </option>
          ))}
        </select>
      </Container>

      <Navbar bg="dark" variant="dark" fixed="bottom">
        <Container>
          <Navbar.Text>&copy; 2023 React Restaurant Finder</Navbar.Text>
        </Container>
      </Navbar>
    </>
  );
}
