import { useState, useEffect } from "react";
import Axios from "axios";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";

export default function App() {
  const [restaurants, setRestaurants] = useState([]);
  const [selectedCity, setSelectedCity] = useState("");
  const [selectedRestaurant, setSelectedRestaurant] = useState("");
  const [restaurantLocation, setRestaurantLocation] = useState({
    lat: null,
    lng: null,
  });

  useEffect(() => {
    Axios.get("http://localhost:3001/restaurants").then((res) => {
      setRestaurants(res.data);
    });
  }, []);

  const handleCitySelect = (event) => {
    setSelectedCity(event.target.value);
    setSelectedRestaurant("");
    setRestaurantLocation({ lat: null, lng: null });
  };

  const handleRestaurantSelect = (event) => {
    setSelectedRestaurant(event.target.value);
    const selectedRestaurantData = restaurants.find(
      (restaurant) => restaurant.id === event.target.value
    );
    setRestaurantLocation({
      lat: selectedRestaurantData.location.lat,
      lng: selectedRestaurantData.location.lng,
    });
  };

  useEffect(() => {
    if (selectedCity) {
      Axios.get(`http://localhost:3001/restaurants/${selectedCity}`).then(
        (res) => {
          setRestaurants(res.data);
        }
      );
    }
  }, [selectedCity]);

  return (
    <>
      <select value={selectedCity} onChange={handleCitySelect}>
        <option value="">Select a city</option>
        <option value="Casablanca">Casablanca</option>
        <option value="Tanger">Tanger</option>
        <option value="El Jadida">El jadida</option>
        {/* add more cities as needed */}
      </select>

      <select value={selectedRestaurant} onChange={handleRestaurantSelect}>
        <option value="">Select a restaurant</option>
        {restaurants.map((restaurant) => (
          <option value={restaurant.id} key={restaurant.id}>
            {restaurant.title}
          </option>
        ))}
      </select>

      {restaurantLocation.lat && restaurantLocation.lng && (
        <MapContainer
          center={[restaurantLocation.lat, restaurantLocation.lng]}
          zoom={13}
          style={{ height: "400px", width: "100%" }}
        >
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Marker position={[restaurantLocation.lat, restaurantLocation.lng]}>
            <Popup>
              <strong>{selectedRestaurant}</strong>
            </Popup>
          </Marker>
        </MapContainer>
      )}
    </>
  );
}
